void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 color = vec4(0.0);

    for (int i = 0; i < 3; i++){
        for (int j = 0; j < 3; j++){
            color += texture(iChannel0, vec2(fragCoord.x + float(i - 1), fragCoord.y + float(j - 1))/iResolution.xy);
        }
    }

    color /= 9.0;

    // Output to screen
    fragColor = color;
}
