//Post-processing.

// Narkowicz 2015, "ACES Filmic Tone Mapping Curve"
vec3 Tonemap_ACES(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;

    color = Tonemap_ACES(color);
    color = pow(color, vec3(0.4545));

    fragColor = vec4(color,1.0);
}

//BRDF model comes from https://google.github.io/filament/Filament.html

#define DegToRad 0.01745329252
#define INFINITY 1e10
#define EPSILON 0.0001

#define PI 3.14159265359
#define INV_PI 0.31830988618
#define SQRT2_2 0.70710678118

#define DEFAULT_REFLECTANCE 0.04

/////////////////////////////////////////
//             Structures              //
/////////////////////////////////////////

struct Camera
{
    vec3 position;
    vec3 target;
    vec3 upVec;
    float fovy;
};

struct Ray
{
    vec3 origin;
    vec3 direction;
};

struct Sphere
{
    int materialID;

    vec3 center;
    float radius;
};

struct Quad
{
    int materialID;

    vec3 p;
    vec3 w;
    vec3 l;
};

struct Intersection
{
    vec3 position;
    vec3 normal;
};

struct Material
{
    int materialType;
    vec3 baseColor;
    float roughness;
    float metallic;
};

/////////////////////////////////////////
//            Ray Tracing              //
/////////////////////////////////////////

//Random functions
float seed;
float GetRandom(){return fract(sin(seed++)*43758.5453123);}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

Ray InitRay(Camera camera, vec2 fragCoord, vec2 resolution)
{
    vec3 forward = normalize(camera.target - camera.position);
    vec3 right = normalize(cross(camera.upVec, forward));
    vec3 up = cross(forward, right);

    float randomA = GetRandom();
    float randomB = GetRandom();
    vec2 realFragCoord = fragCoord + vec2(randomA, randomB) - 0.5;
    realFragCoord = realFragCoord * 2.0 - resolution.xy;

    vec2 uv = tan(camera.fovy * DegToRad * 0.5) * (realFragCoord / resolution.yy);
    vec3 rayDir = normalize(right * uv.x + up * uv.y + forward);

    return Ray(camera.position, rayDir);
}

bool SolveQuadratic(float a, float b, float c, out float x0, out float x1)
{
    float discriminant = b * b - 4.0 * a * c;
    if(discriminant < 0.0){ return false;}

    float rootDisc = sqrt(discriminant);
    float q = (b > 0.0) ? -0.5 * (b + rootDisc) : -0.5 * (b - rootDisc);
    float temp0 = q / a;
    float temp1 = c / q;
    if (temp1 > temp0)
    {
        x0 = temp0;
        x1 = temp1;   
    }
    else
    {
        x0 = temp1;
        x1 = temp0;
    }
    return true;  
}

bool RayQuadIntersect(Ray ray, Quad quad, out float t, out Intersection intersection)
{
    vec3 edge1 = quad.w;
    vec3 edge2 = quad.l;
    vec3 h = cross(ray.direction, edge2);
    float a = dot(edge1, h);
    if(abs(a) < EPSILON) {return false;}

    float f = 1.0 / a;
    vec3 s = ray.origin - quad.p;
    float u = f * dot(s, h);
    //I modified this a little bit to ensure there could be an intersection near edges.
    //original:
    //if(u < 0.0 || u > 1.0) {return false;}
    if(u < -EPSILON || u > 1.0 + EPSILON) {return false;}

    vec3 q = cross(s, edge1);
    float v = f * dot(ray.direction, q);
    //origin:
    //if(v < 0.0 || v > 1.0) {return false;}
    if(v < -EPSILON || v > 1.0 + EPSILON) {return false;}

    t = f * dot(edge2, q);
    if(t < EPSILON) {return false;}

    intersection.position = ray.origin + t * ray.direction;
    intersection.normal = normalize(cross(edge1, edge2));
    return true;
}

bool RaySphereIntersect(Ray ray, Sphere sphere, out float t, out Intersection intersection)
{
    bool hasIntersection = false;

    float t0, t1;
    vec3 l = ray.origin - sphere.center;
    float a = dot(ray.direction, ray.direction);
    float b = 2.0 * dot(ray.direction, l);
    float c = dot(l, l) - sphere.radius * sphere.radius;
    if (!SolveQuadratic(a, b, c, t0, t1)) {return false;}

    if(t1 > 0.0)
    {
        t = t1;
        hasIntersection = true;
    }

    if(t0 > 0.0)
    {
        t = t0;
        hasIntersection = true;
    }

    intersection.position = ray.origin + t * ray.direction;
    intersection.normal = normalize(intersection.position - sphere.center);
    return hasIntersection;
}

bool LightRay(Quad light, Intersection intersection, int j, int k, float ratio, out Ray lightRay, out vec3 rayTarget)
{
    float randomA = GetRandom();
    float randomB = GetRandom();
    rayTarget = light.p + light.w * (float(j) + randomA) * ratio + light.l * (float(k) + randomB) * ratio;
    vec3 lightNormal = cross(light.w, light.l);
    lightRay.direction = normalize(rayTarget - intersection.position);
    lightRay.origin = intersection.position + lightRay.direction * 0.01;  

    bool hasLight = (dot(lightRay.direction, lightNormal) < 0.0);
    return hasLight;
}

/////////////////////////////////////////
//              Sampling               //
/////////////////////////////////////////

vec3 SampleHemiSphere()
{
    float theta = acos(GetRandom());
    float phi = 2.0 * PI * GetRandom();
    float sinTheta = sin(theta);

    return vec3(cos(phi) * sinTheta, sin(phi) * sinTheta, cos(theta));
}

vec3 SampleHemiSphereGGX(float roughness)
{
    float randomA = GetRandom();
    float randomB = GetRandom();

    float theta = acos(sqrt((1.0 - randomA) / ((roughness * roughness - 1.0) * randomA + 1.0)));
    float phi = 2.0 * PI * randomB;
    float sinTheta = sin(theta);

    return vec3(cos(phi) * sinTheta, sin(phi) * sinTheta, cos(theta));
}

Ray GetRandomRay(vec3 lastRayDir, vec3 position, vec3 normal)
{
    Ray ray;

    vec3 s = SampleHemiSphere();
    s.z = abs(s.z);

    vec3 w = normal;
    vec3 u, v;
    if(abs(dot(normal, vec3(0.0, 1.0, 0.0))) <= SQRT2_2)
    {
        u = normalize(cross(vec3(0.0, 1.0, 0.0), w));
        v = cross(w, u);
    }
    else
    {
        v = normalize(cross(w, vec3(1.0, 0.0, 0.0)));
        u = cross(v, w);
    }
    ray.direction = s.x * u + s.y * v + s.z * w;
    ray.origin = position + ray.direction * 0.001;

    return ray;    
}

Ray GetRandomRay_GGX(vec3 lastRayDir, vec3 position, vec3 normal, float roughness, float t)
{
    Ray ray;

    float randomValue = GetRandom();

    vec3 w = normal;
    vec3 s, u, v;

    if(randomValue < t)
    {
        //Sample specular, half vector.
        s = SampleHemiSphereGGX(roughness);
    }
    else
    {
        //Sample diffuse, sample vector;
        s = SampleHemiSphere();
    }

    s.z = abs(s.z);

    if(abs(dot(normal, vec3(0.0, 1.0, 0.0))) <= SQRT2_2)
    {
        u = normalize(cross(vec3(0.0, 1.0, 0.0), w));
        v = cross(w, u);
    }
    else
    {
        v = normalize(cross(w, vec3(1.0, 0.0, 0.0)));
        u = cross(v, w);
    }

    if(randomValue < t)
    {
        vec3 halfVec = s.x * u + s.y * v + s.z * w;
        ray.direction = reflect(lastRayDir, halfVec);
    }
    else
    {
        ray.direction = s.x * u + s.y * v + s.z * w;
    }

    ray.origin = position + ray.direction * 0.001;

    return ray;
}

Ray GetRandomRay_NEE(Quad light, vec3 position, vec3 normal, out float dist)
{
    float randomA = GetRandom();
    float randomB = GetRandom();
    vec3 lightTarget = light.p + light.w * randomA + light.l * randomB;

    vec3 lightOrigin = position + 0.001 * normal;
    vec3 lightDirection = normalize(lightTarget - lightOrigin);

    Ray lightRay = Ray(lightOrigin, lightDirection);
    dist = length(lightTarget - lightRay.origin);
    return lightRay;
}

/////////////////////////////////////////
//                BRDF                 //
/////////////////////////////////////////

// Bruce Walter et al. 2007. Microfacet Models for Refraction through Rough Surfaces. Proceedings of the Eurographics Symposium on Rendering.
float D_GGX(float NoH, float roughness) {
    float a = NoH * roughness;
    float k = roughness / (1.0 - NoH * NoH + a * a);
    return k * k * INV_PI;
}

// Eric Heitz. 2014. Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs. Journal of Computer Graphics Techniques, 3 (2).
float V_SmithGGXCorrelated(float NoV, float NoL, float roughness) {
    float a2 = roughness * roughness;
    float GGXV = NoL * sqrt(NoV * NoV * (1.0 - a2) + a2);
    float GGXL = NoV * sqrt(NoL * NoL * (1.0 - a2) + a2);
    return 0.5 / (GGXV + GGXL);
}

// Christophe Schlick. 1994. An Inexpensive BRDF Model for Physically-Based Rendering. Computer Graphics Forum, 13 (3), 233â€“246.
float F_Schlick(float LoH, float f0, float f90) {
    return f0 + (f90 - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F_Schlick(float LoH, vec3 f0, vec3 f90) {
    return f0 + (f90 - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F_Schlick(float LoH, vec3 f0) {
    return f0 + (vec3(1.0) - f0) * pow(1.0 - LoH, 5.0);
}

vec3 F0(vec3 baseColor, float metallic) {
    return DEFAULT_REFLECTANCE * (1.0 - metallic) + baseColor * metallic;
}

// Specular BRDF
vec3 Fr(float NoV, float NoL, float NoH, float LoH, float roughness, vec3 f0) {
    float D = D_GGX(NoH, roughness);
    vec3 F = F_Schlick(LoH, f0);
    float V = V_SmithGGXCorrelated(NoV, NoL, roughness);
    return D * F * V;
}   

float Fd_Lambert() {
    return INV_PI;
}

// Diffuse BRDF
// Brent Burley. 2012. Physically Based Shading at Disney. Physically Based Shading in Film and Game Production, ACM SIGGRAPH 2012 Courses.
float Fd_Burley(float NoV, float NoL, float LoH, float roughness) {
    float f90 = 0.5 + 2.0 * roughness * LoH * LoH;
    float lightScatter = F_Schlick(NoL, 1.0, f90);
    float viewScatter = F_Schlick(NoV, 1.0, f90);
    return lightScatter * viewScatter * INV_PI;
}

vec3 EvaluateBRDF(vec3 wi, vec3 wo, Material material, vec3 normal)
{
    vec3 h = normalize(wi + wo);
    float NdotV = abs(dot(normal, wo)) + 1e-5;
    float NdotL = max(dot(normal, wi), 0.0);
    float NdotH = max(dot(normal, h), 0.0);
    float LdotH = max(dot(wi, h), 0.0);

    float roughness = material.roughness * material.roughness;
    vec3 f0 = F0(material.baseColor, material.metallic);
    vec3 diffuse = material.baseColor * (1.0 - material.metallic);

    vec3 fr = Fr(NdotV, NdotL, NdotH, LdotH, roughness, f0);    
    vec3 fd = Fd_Burley(NdotV, NdotL, LdotH, roughness) * diffuse;    

    return (fr + fd) * NdotL;
}

vec3 EvaluateBRDF(vec3 wi, vec3 wo, Quad light, float dist, Material material, vec3 normal)
{
    vec3 h = normalize(wi + wo);
    float NdotV = abs(dot(normal, wo)) + 1e-5;
    float NdotL = max(dot(normal, wi), 0.0);
    float NdotH = max(dot(normal, h), 0.0);
    float LdotH = max(dot(wi, h), 0.0);

    float roughness = material.roughness * material.roughness;
    vec3 f0 = F0(material.baseColor, material.metallic);
    vec3 diffuse = material.baseColor * (1.0 - material.metallic);

    vec3 fr = Fr(NdotV, NdotL, NdotH, LdotH, roughness, f0);
    vec3 fd = Fd_Burley(NdotV, NdotL, LdotH, roughness) * diffuse;

    vec3 lightNormal = cross(light.w, light.l);
    float area = length(lightNormal);
    lightNormal = normalize(lightNormal);
    float G = dot(-wi, lightNormal) * NdotL / (dist * dist);
    G = max(G, 0.0);

    return (fr + fd) * G * area;
}

/////////////////////////////////////////
//               PDFs                  //
/////////////////////////////////////////

float PDF_Lambert(vec3 wi, vec3 normal){
    return INV_PI * max(dot(normal, wi), 0.0);
}

float PDF_GGX(vec3 wi, vec3 wo, vec3 normal, float roughness)
{
    vec3 h = normalize(wi + wo);
    float NdotH = max(dot(normal, h), 0.0);

    return D_GGX(NdotH, roughness) * NdotH / (4.0 * max(dot(wo, h), 0.001));
}

float PDF_Quad(Quad light, Ray ray)
{
    float t;
    Intersection intersection;

    bool hasIntersect = RayQuadIntersect(ray, light, t, intersection);
    if(hasIntersect)
    {
        vec3 lightNormal = cross(light.w, light.l);
        float area = length(lightNormal);
        lightNormal = normalize(lightNormal);

        return t * t / (area * abs(dot(lightNormal, -ray.direction)));
    }
    else
    {
        return 0.0;
    }    
}

//Cornell box path tracing with:
//    1 random sample per pixel,
//    next event estimation (1 random sample towards light),
//    russian roulette,
//    ggx brdf,
//    and multiple importance sampling.

//Highly unoptimized path tracing, any advice would be greatly appreciated!
//Combining triangles to quads saves lots of compiling time!
//Thanks to spalmer, by replacing `0` with `min(0, iFrame)` in for loops, the compile time is greatly reduced!

#define CUBE_COUNT 1
#define WALL_COUNT 5
#define SPHERE_COUNT 1
#define LIGHT_COUNT 1

Quad lights[LIGHT_COUNT];
Quad quads[CUBE_COUNT * 6 + WALL_COUNT + LIGHT_COUNT];
Sphere spheres[SPHERE_COUNT];
Material materials[6];

///////////////////////////////////////////////////
//             Scene Build Functions             //
///////////////////////////////////////////////////

void CubeToQuads(int materialID, vec3 center, vec3 xAxis, vec3 size, inout int offset)
{
    vec3 u = normalize(xAxis);
    vec3 w = normalize(cross(u, vec3(0.0, 1.0, 0.0)));
    vec3 v = cross(w, u);

    u *= size.x;
    v *= size.y;
    w *= size.z;

    vec3 p0 = center - (u + v + w) * 0.5;   
    vec3 p6 = center + (u + v + w) * 0.5;

    quads[offset    ] = Quad(materialID, p0, u, w);
    quads[offset + 1] = Quad(materialID, p0, v, u);
    quads[offset + 2] = Quad(materialID, p0, w, v);

    quads[offset + 3] = Quad(materialID, p6, -w, -u);
    quads[offset + 4] = Quad(materialID, p6, -u, -v);
    quads[offset + 5] = Quad(materialID, p6, -v, -w);

    offset += 6;
}


void MakeScene()
{
    //floor
    quads[0] = Quad(1, vec3(-6.0, 0.0, -6.0), vec3(0.0, 0.0, 12.0), vec3(12.0, 0.0, 0.0));

    //ceil
    quads[1] = Quad(1, vec3(-6.0, 10.0, -6.0), vec3(12.0, 0.0, 0.0), vec3(0.0, 0.0, 12.0));

    //left wall
    quads[2] = Quad(2, vec3(-6.0, 0.0, -6.0), vec3(0.0, 10.0, 0.0), vec3(0.0, 0.0, 12.0));

    //right wall
    quads[3] = Quad(3, vec3(6.0, 0.0, -6.0), vec3(0.0, 0.0, 12.0), vec3(0.0, 10.0, 0.0));

    //back wall
    quads[4] = Quad(1, vec3(-6.0, 0.0, 6.0), vec3(0.0, 10.0, 0.0), vec3(12.0, 0.0, 0.0));

    int offset = 5;
    //cube 6-11
    CubeToQuads(4, vec3(-2.0, 3.0, 3.0), vec3(1.0, 0.0, 0.5), vec3(4.0, 8.0, 4.0), offset);

    //sphere
    spheres[0] = Sphere(5, vec3(2.0, 2.0, 0.0), 2.0);

    //light 12
    lights[0] = Quad(0, vec3(-2.0, 9.5, -2.0), vec3(4.0, 0.0, 0.0), vec3(0.0, 0.0, 4.0));
    quads[11] = lights[0];

    //materials
    materials[0] = Material(0, vec3(5.0, 5.0, 5.0), 0.0, 0.0);
    materials[1] = Material(1, vec3(0.7, 0.7, 0.4), 0.9, 0.0);
    materials[2] = Material(1, vec3(1.0, 0.0, 0.0), 0.9, 0.0);
    materials[3] = Material(1, vec3(0.0, 1.0, 0.0), 0.9, 0.0);
    materials[4] = Material(1, vec3(0.7, 0.7, 0.4), 0.1, 0.7);
    materials[5] = Material(1, vec3(1.0, 0.64, 0.05), 0.6, 0.9);
}

///////////////////////////////////////////////////
//             Scene Related Tracing             //
///////////////////////////////////////////////////

bool TraceScene(Ray ray, out Intersection intersection, out int materialID)
{
    float d = INFINITY;
    bool hasHit = false;
    for(int i = min(0, iFrame); i < quads.length(); i++)
    {
        float t;
        Intersection tempIntersection;
        bool isHit = RayQuadIntersect(ray, quads[i], t, tempIntersection);
        if(isHit && t < d)
        {
            d = t;
            hasHit = true;

            materialID = quads[i].materialID;
            intersection = tempIntersection;
        }
    }

    for(int i = min(0, iFrame); i < spheres.length(); i++)
    {
        float t;
        Intersection tempIntersection;
        bool isHit = RaySphereIntersect(ray, spheres[i], t, tempIntersection);
        if(isHit && t < d)
        {
            d = t;
            hasHit = true;

            materialID = spheres[i].materialID;
            intersection = tempIntersection;
        }
    }

    return hasHit;
}

bool TraceLight(Ray ray, int lightMaterialID)
{
    float d = INFINITY;
    int materialID = -1;
    for(int i = min(0, iFrame); i < quads.length(); i++)
    {
        float t;
        Intersection tempIntersection;
        bool isHit = RayQuadIntersect(ray, quads[i], t, tempIntersection);
        if(isHit && t < d)
        {
            d = t;

            materialID = quads[i].materialID;
        }
    }

    for(int i = min(0, iFrame); i < spheres.length(); i++)
    {
        float t;
        Intersection tempIntersection;
        bool isHit = RaySphereIntersect(ray, spheres[i], t, tempIntersection);
        if(isHit && t < d)
        {
            d = t;

            materialID = spheres[i].materialID;
        }
    }

    return materialID == lightMaterialID;
}

///////////////////////////////////////////////////
//             Scene Related PDFs                //
///////////////////////////////////////////////////

float PDF_NEE(Ray ray)
{
    float pdfNEE = 0.0;
    for (int i = min(0, iFrame); i < lights.length(); i++)
    {
        float pdfLight = PDF_Quad(lights[i], ray);
        pdfNEE += pdfLight;
    }
    return pdfNEE / float(lights.length());
}

float PDF_BRDF(vec3 wi, vec3 wo, vec3 normal, float roughness, float t)
{
    return (1.0 - t) * PDF_Lambert(wi, normal) + t * PDF_GGX(wi, wo, normal, roughness);
}

void WeightPDF(Ray ray, vec3 wo, vec3 normal, float roughness, float t, out float weightNEE, out float weightBRDF)
{
    float pdfNEE = PDF_NEE(ray);
    float pdfBRDF = PDF_BRDF(ray.direction, wo, normal, roughness, t);

    float squareSum = pdfNEE * pdfNEE + pdfBRDF * pdfBRDF;
    if(squareSum == 0.0)
    {
        weightNEE = 1.0;
        weightBRDF = 0.0;
    }
    else
    {
        weightNEE = pdfNEE / squareSum;
        weightBRDF = pdfBRDF / squareSum;
    }
}

///////////////////////////////////////////////////
//                Main Function                  //
///////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.0);

    seed = hash12(fragCoord) + iTime;
    float randomValue = GetRandom();

    Camera camera = Camera(vec3(0.0, 4.0, -12.0), vec3(0.0, 4.0, 0.0), vec3(0.0, 1.0, 0.0), 45.0);

    Ray ray = InitRay(camera, fragCoord, iResolution.xy);
    MakeScene();

    Intersection intersection;
    int materialID;
    bool isHit = TraceScene(ray, intersection, materialID);
    if(isHit)
    {
        int depth = 1;
        Material material = materials[materialID];

        if(material.materialType == 0)
        {
            //Hit light directly.
            color = material.baseColor;
        }
        else
        {
            vec3 lastBRDF = vec3(1.0);

            bool terminateRay = false;
            while(!terminateRay)
            {
                vec3 lastRayDir = ray.direction;
                float roughness = material.roughness * material.roughness;

                vec3 F0 = F0(material.baseColor, material.metallic);
                vec3 diffuse = material.baseColor * (1.0 - material.metallic);
                float F0Sum = F0.x + F0.y + F0.z;
                float diffuseSum = diffuse.x + diffuse.y + diffuse.z;
                float t = max(F0Sum / (diffuseSum + F0Sum), 0.25);

                //Next event estimation for directional light.
                vec3 directBRDF = vec3(0.0);

                for (int i = 0; i < lights.length(); i++)
                {
                    float dist;
                    Ray lightRay = GetRandomRay_NEE(lights[i], intersection.position, intersection.normal, dist);

                    float weightNEE, weightIndirect;
                    WeightPDF(lightRay, -lastRayDir, intersection.normal, roughness, t, weightNEE, weightIndirect);

                    vec3 lightNormal = cross(lights[i].w, lights[i].l);
                    float LNdotL = dot(lightNormal, -lightRay.direction);

                    int lightMaterialID = lights[i].materialID;
                    if(LNdotL > 0.0)
                    {
                        bool isShaded = TraceLight(lightRay, lightMaterialID);
                        if(isShaded)
                        {    
                            directBRDF += EvaluateBRDF(lightRay.direction, -lastRayDir, material, intersection.normal)
                                          * materials[lightMaterialID].baseColor * weightNEE;
                        }                    
                    }
                }

                color += lastBRDF * directBRDF;

                //GGX importance sampling for indirectional light.
                vec3 indirectBRDF = vec3(0.0);

                //Russian roulette
                float randomA = GetRandom();
                float probability = 1.0 - min(max(max(lastBRDF.r, lastBRDF.g), lastBRDF.b), 1.0);
                if(randomA >= probability)
                {
                    //GGX importance sampling new ray.
                    ray = GetRandomRay_GGX(lastRayDir, intersection.position, intersection.normal, roughness, t);
                    float weightNEE, weightIndirect;
                    WeightPDF(ray, -lastRayDir, intersection.normal, roughness, t, weightNEE, weightIndirect);

                    Intersection ggxIntersection;
                    int ggxMaterialID;
                    bool ggxHit = TraceScene(ray, ggxIntersection, ggxMaterialID);
                    if(ggxHit)
                    {
                        Material ggxMaterial = materials[ggxMaterialID];
                        indirectBRDF = EvaluateBRDF(ray.direction, -lastRayDir, material, intersection.normal) / (1.0 - probability);
                        lastBRDF *= indirectBRDF * weightIndirect;

                        if(ggxMaterial.materialType == 0)
                        {
                            //Reached light, terminate ray.
                            float frontLight = dot(ray.direction, ggxIntersection.normal) < 0.0 ? 1.0 : 0.0;
                            color += lastBRDF * ggxMaterial.baseColor * frontLight;
                            terminateRay = true;
                        }
                        else
                        {
                            //Start a new iteration.
                            intersection = ggxIntersection;
                            materialID = ggxMaterialID;
                            material = ggxMaterial;
                            depth ++;
                        }
                    }
                    else
                    {
                        //GGX importance sampling missed, terminate ray.
                        terminateRay = true;
                    }
                }
                else
                {
                    //Russian roulette failed, terminate ray.
                    terminateRay = true;
                }
            } 
        }
    }

    fragColor = vec4(color, 1.0);
}

//Accumulate history colors.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);

    if(iFrame == 0)
    {
        fragColor = color; return;
    }

    vec2 uv = fragCoord / iResolution.xy;
    vec3 bufferA = texture(iChannel0, uv).rgb;
    vec4 bufferB = texture(iChannel1, uv);

    if(iMouse.z > 0.0)
    {
        fragColor = vec4(bufferA, 1.0);
    }
    else
    {
        bufferB.a += 1.0;
        color.rgb = mix(bufferB.rgb, bufferA, 1.0 / bufferB.a);
        fragColor = vec4(color.rgb, bufferB.a);
    }

}
