vec3 circle(vec2 uv,vec2 c,float r,float speed,float width,float offset){
    vec3 col = vec3(0.);
    float dis = length(uv-c);
    if((mod(offset+iTime*speed,r)-width)<dis&&dis<mod(offset+iTime*speed,r)){
      col = vec3(1.0,0.0,0.0);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y-vec2(iResolution.x/iResolution.y,1.0)*0.5;
    int num = 6;
    float r = 0.5;
    float speed = -0.1;
    float width = 0.01;
    float split = 0.2;
    vec3 col = vec3(0.0);
    for(int i=0;i<num;i++){
      col+=circle(uv,vec2(0.),r,speed,width,split*float(i));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
