#define pi 3.14159265359


const float max_offset = 0.15;
const float speed = 4.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 forground = texture(iChannel0, uv);
    vec4 background = texture(iChannel1, uv);

    float offset = max_offset * acos(cos(speed * iTime)) / pi;

    vec4 forground_copy_1 = texture(iChannel0, uv + vec2( offset, 0.0));
    vec4 forground_copy_2 = texture(iChannel0, uv + vec2(-offset, 0.0));
    vec4 forground_copy_3 = texture(iChannel0, uv + vec2( 2.0 * offset, 0.0));
    vec4 forground_copy_4 = texture(iChannel0, uv + vec2(-2.0 * offset, 0.0));


    if (forground_copy_4.a > 0.0) {
        forground_copy_4.a = 0.5;
    }
    if (forground_copy_3.a > 0.0) {
        forground_copy_4 = forground_copy_3;
        forground_copy_4.a = 0.6;
    }
    if (forground_copy_2.a > 0.0) {
        forground_copy_4 = forground_copy_2;
        forground_copy_4.a = 0.7;
    }
    if (forground_copy_1.a > 0.0) {
        forground_copy_4 = forground_copy_1;
        forground_copy_4.a = 0.8;
    }
    if (forground.a > 0.0) {
        forground_copy_4 = forground;
    }

    fragColor = mix(background, forground_copy_4, forground_copy_4.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
