// Fork of "for loop" by cghow. https://shadertoy.com/view/7lcXDH
// 2021-12-09 03:51:48



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
uv -= .5;
uv.x*= iResolution.x/iResolution.y;
//uv*= uv.x*10.;
vec3 col1 = vec3(0);
for(float i=0.; i<.5; i+=.01){
uv.x+= clamp(sin(2.*iTime*.01)*.1,-.5,.5)*.15;
uv.y+= clamp(cos(iTime+i*5.)*.1,-.5,.5)*.15;
float d = length(uv);
float s = step(d,i)*.01;
col1+=s;
}
vec3 col2 = vec3(0);
for(float i=0.; i<.5; i+=.01){
uv.x-= clamp(sin(2.*iTime*.01)*.1,-.5,.5)*.15;
uv.y-= clamp(cos(iTime+i*5.)*.1,-.5,.5)*.15;
float d = length(uv);
float s = step(d,i-.02)*.01;
col2+=s;
}
vec3 color = vec3(sin(iTime),cos(iTime*.9),sin(iTime*.8));
vec3 col = col1+col2;
col = smoothstep(.0,5.,col)*10.;
col*=color;

    fragColor = vec4(col,1.0);
}
