void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

vec4 gameOfLife(int channel, vec2 fragCoord)
{
    vec4 fragColor;

        // Get original noise texture to start cellular sim.
    vec4 initialNoise = texture(iChannel1, fragCoord/iResolution.xy);

    // Only start after 1 frame.
    if(iFrame < 10)
    {
        fragColor = initialNoise;
    }
    else
    {
        // Get cell value last frame.
        vec2 uv = fragCoord/iResolution.xy;
        vec4 lastFrame = texture(iChannel0, uv);


        // Get all 8 neighbour cells.
        vec2 Left = vec2(-1.0, 0.0);
        vec2 Top = vec2(0.0, -1.0);

        vec4 leftNeighbour = texture(iChannel0, (fragCoord + Left)/iResolution.xy);
        vec4 rightNeighbour = texture(iChannel0, (fragCoord - Left)/iResolution.xy);
        vec4 topNeighbour = texture(iChannel0, (fragCoord + Top)/iResolution.xy);
        vec4 bottomNeighbour = texture(iChannel0, (fragCoord - Top)/iResolution.xy);
        vec4 leftTopNeighbour = texture(iChannel0, (fragCoord + Left + Top)/iResolution.xy);
        vec4 rightTopNeighbour = texture(iChannel0, (fragCoord - Left + Top)/iResolution.xy);
        vec4 rightBottomNeighbour = texture(iChannel0, (fragCoord - Top - Left)/iResolution.xy);
        vec4 leftBottomNeighbour = texture(iChannel0, (fragCoord - Top + Left)/iResolution.xy);

        // Count living cells.
        int count = 0;
        if(leftNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(rightNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(topNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(bottomNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(leftTopNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(rightTopNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(leftBottomNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 
        if(rightBottomNeighbour[channel] > 0.5)
        {
            count = count + 1;
        } 

        // If 2 or 3 living neighbours, live, else die.
        if(lastFrame[channel] > 0.5)
        {
            if(count > 1 && count < 4)
            {
                fragColor[channel] = 1.0;
            }
            else
            {
                fragColor[channel]= 0.0;
            }
        }
        else
        {
            // If dead, and 3 living neighbours, get born.
            if(count == 3)
            {
               fragColor[channel] = 1.0;
            }
        }

    }

    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.r = gameOfLife(0, fragCoord).r;
    fragColor.g = gameOfLife(1, fragCoord).g;
    fragColor.b = gameOfLife(2, fragCoord).b;
    fragColor.a = 1.0;
}
