}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 a = +A(u+vec2( 1, 0))
             +A(u+vec2( 0, 1))
             +A(u+vec2(-1, 0))
             +A(u+vec2( 0,-1))
             +A(u+vec2( 1, 1))
             +A(u+vec2(-1, 1))
             +A(u+vec2( 1,-1))
             +A(u+vec2(-1,-1))
             +A(u+vec2( 0, 0));
    //a = 9.-a;
    float s = round(dot(a,vec4(1,0,0,0)));
    float o = 0.;
    if(s==0.){o = 1.;}
    if(s==1.){o = 1.;}
    if(s==2.){o = 0.;}
    if(s==3.){o = 1.;}
    if(s==4.){o = 1.;}
    if(s==5.){o = 1.;}
    if(s==6.){o = 1.;}
    if(s==7.){o = 0.;}
    if(s==8.){o = 0.;}
    if(s==9.){o = 0.;}
    if(iFrame==0||iMouse.z>.5)
    {
        vec2 v = 1.*(u        *2.-iResolution.xy)/iResolution.y;
        vec2 m = 1.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;
        o = floor(fract(cos(dot(u,vec2(234.76543,iTime+22.5363)))*2467.5678)+.97);
        o *= step(dot(v,v),dot(m,m)*.5);
    }
    fragColor = vec4(o,A(u+vec2(0,0)).xyz);
}
