void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float pos[4];
    vec4 colors[5];
    pos[0] = 0.25;
    pos[1] = 0.5;
    pos[2] = 0.75;
    pos[3] = 1.0;

    colors[0] = vec4(1.0, 0.0, 0.0, 1.0);
    colors[1] = vec4(0.0, 1.0, 0.0, 1.0);
    colors[2] = vec4(0.0, 0.0, 1.0, 1.0);
    colors[3] = vec4(0.0, 1.0, 1.0, 1.0);
    colors[4] = vec4(1.0, 0.0, 1.0, 1.0);

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color;
        color = mix(colors[0], colors[1] , smoothstep(0.0, pos[0], uv.x));
        color = mix(color, colors[2] , smoothstep(pos[0], pos[1], uv.x));
        color = mix(color, colors[3] , smoothstep(pos[1], pos[2], uv.x));
        color = mix(color, colors[4] , smoothstep(pos[2], pos[3], uv.x));

    //sRGB
    //fragColor = pow(color, vec4(1.0/2.2));

    //Linar
    fragColor = color;
}
