void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 portalUV = (uv-0.5)* vec2(8.0,2.5);

    float ring1 = sat((1.0-length(portalUV))*20.0);

    float ring2 = sat((length(portalUV*vec2(1.15,1.125))-1.025)*20.0);

    float ring = min(ring1,ring2);

    vec3 tex = texture(iChannel0, uv).rgb;

    fragColor = ring * vec4(0.15,0.75,1.0,1.0);
