}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 portalUV = (uv-0.5)* vec2(8.0,2.5) ;

    vec2 tangent = normalize(portalUV);

    vec2 polar = vec2((atan(portalUV.x, portalUV.y)/Pi/2.0+0.5)*1.0+iTime*-0.05,length(portalUV)*0.25+iTime*-0.01);

    vec2 turbulence = texture(iChannel2, polar).xy * 2.0 -1.0;

    vec3 image = texture(iChannel1, uv+turbulence*0.005).rgb*2.75;

    vec3 buffer = texture(iChannel0, uv+turbulence*0.0085 + vec2(tangent.y,-tangent.x)*vec2(2.65,10.0)*0.01).rgb * vec3(0.7,0.75,1.0);

    vec3 col = mix(image, buffer, 0.75);

    fragColor = vec4(col,1.0);
}

#define Pi 3.14159265359

float sat(float a)
{
    return clamp(a,0.0,1.0);
}
