float N21(vec2 p){
 	float x=fract(sin(p.x*100.+p.y*168.+floor(iTime))*2368.);

    return x;
}
float dis(vec2 p,vec2 q){
    return max(abs(p.x-q.x),abs(p.y-q.y));
    //return length(p-q);
}
float remap(float a,float b,float x1,float x2,float x){
    return mix(a,b,(x-x1)/(x2-x1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scale = 10.*vec2(iResolution.x/iResolution.y,1.0);
    vec2 newUV=uv*scale;
    vec2 id=floor(newUV);
    vec2 offset=fract(newUV);
    vec2 maskUV=id/scale;
    // Time varying pixel color
    vec3 col=vec3(0.0);
    vec2 lastUV;
    vec2 lastOffset=vec2(0,0);
    float tempScale=0.;
    for(int i=-1;i<=1;i++)
        for(int j=-1;j<=1;j++){
            //获取当前遍历的格子的id
            vec2 idOffset = vec2(i,j);
            vec2 newid= id+idOffset;
            //根据id得到他的缩放参数
            float newid_rand = N21(newid);
            float maskScale=newid_rand*0.5+1.0;
            //首先判断当前像素点是否在放大的方形内，示意图如下图
            //接着判断缩放参数，取大的就好。
            if(dis(offset,idOffset+0.5)*2.<maskScale&&tempScale<maskScale){
                lastUV=newid/scale;     
                tempScale=maskScale;
            }
        }
    fragColor = vec4(vec3(N21(lastUV)),1.0);
    //fragColor = vec4(col,1.0);
}
