vec2 sqr_complex(vec2 ah) 
{
    return vec2(ah.x * ah.x - ah.y * ah.y, 2.0 * ah.x * ah.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0 * ( 2.0 * fragCoord- iResolution.xy ) / iResolution.x;
    vec2 z = vec2(0);
    vec3 color = vec3(0);
    for(int i = 0; i < 64; i++)
    {
        z = sqr_complex(z) + uv;
        if(length(z) > 2.0)
        {
            color = vec3(i)/64.0;
        }

    }

    fragColor = vec4(color,1.0);
}
