#define PI (atan(1.)*4.)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<90.;
        O.xyz+=mix(vec3(1),H(log(s)*.3),.6)*.02*exp(-.3*i*i*e)
    )
    {
        p=g*d+vec3(0,0,iTime*.5);
        p=asin(cos(p*PI/3.))/PI*3.;
        s=3.;
        for(int i;i++<6;)
            p=abs(p-vec3(1.5,1,1.1))-vec3(1.1,1.3,1),
            s*=e=2.2/clamp(dot(p,p),.5,2.3),
            p=abs(p)*e;
        g+=e=length(p.xz)/s;
    }
}
