#define PI 3.1415926538

float stepp(float v, float steps){
    return trunc(v * (steps+1.))/(steps);
}
float flip(float v, float steps){
    return mod(trunc(v * (steps+1.)), 2.0)*2.0 - 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float yfixed = stepp(uv.y, 2.);
    float flip = flip(uv.y, 3.);
    uv.x *= flip;
    float xval2 = (cos(uv.x * PI + iTime*.5))/2.+.5;
    float xval = (cos(uv.x * PI + iTime * .44 + yfixed * 4.2))/2.+.5;
    float yval = (cos(uv.x * PI + iTime*.9 + yfixed * 3.))/2.+.5;

    float r = stepp(xval, 7.)/2. * stepp(yval, 8.) + stepp(xval2, 16.) * yfixed;
    float g = stepp(xval, 16.)/2. * stepp(yval, 8.) + stepp(xval2, 15.) * yfixed * stepp(yval+xval, 8.) / 2.;
    float b = stepp(xval, 16.)/2. * (stepp(yval, 7.)*.5+.5) + stepp(xval2, 16.) * yfixed * stepp(yval+xval, 16.) / 2.;

    fragColor = vec4(r, g, b,1.0);
}
