#define M_PI 3.1415926535897932384626433832795

const int maxIter = 2000;


vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

int iterate(vec2 a, vec2 c) {
    vec2 prev = a;
    vec2 square;
    for(int i = 0; i<maxIter; i++) {
        square = vec2(
            pow(prev.x, 2.0) - pow(prev.y, 2.0),
            2.0 * prev.x * prev.y
        ) + c;
        if (length(square) > 1e10) {
            return i;
        }
        prev = square;
    }
    return maxIter;
}

const float speed = 6.;
const float spiralSpeed = 20.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = vec2(0, 0);
    if (iMouse.z > 0.) {
        c = iMouse.xy/iResolution.xy*4.0-2.0;
    } else {
        float rad = .2 + 1.4 * (cos(float(iTime/spiralSpeed))+.5)/3.;
        c = vec2(cos(float(iTime/speed))*rad*1.5-1., sin(float(iTime/speed))*rad);
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec2 suv = uv * 4.0 - 2.0;
    suv = suv / vec2(1.0, 1.8); 

    int iterations = iterate(suv, c);
    float startCol = .6;
    float rate = float(iterations) / float(maxIter);
    float col = iterations < 50 ? mod(startCol + float(rate), 1.0) : mod(startCol + float(iterations)/600., 1.0);
    float intensity = iterations < 50 ? float(iterations) / 50. : .3 + .7 * rate;

    fragColor = vec4(hsl2rgb(vec3(col, 1, intensity)), 1);
}
