void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ani = (sin(iTime)+1.)/2.;

    vec2 u = uv*2.-1.;
    vec2 v = abs(u)-ani;

    float a = 1.;
    if (abs(u.x) > ani && abs(u.y) > ani) {
        a = 1.- smoothstep(0.99-ani, 1.01-ani, length(v));
    }
    vec4 color = texture(iChannel0, uv);

    // Output to screen
    fragColor = color*a;
}
