void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv-vec2(0.5))*2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 dir = vec3(uv, 1.0);
    dir /= length(dir.xy);
    vec2 pos = vec2(dir.z*2.0, (atan(dir.x, dir.y)/3.14159+1.0)*0.5);
    pos.x -= 1.;
    vec3 lamp = vec3(1.0, 1.0, -1.0)*0.5;

    vec3 normal = vec3(-dir.xy, 0.0);
    vec3 lamp_dir = normalize(lamp-dir);

    if(pos.x<0.0 || pos.x > 1.0){
        fragColor = vec4(0.0);
    }else{
        fragColor = texture(iChannel0, pos) * clamp(dot(lamp_dir, normal), 0.0, 1.0);
    }

}
