#define PI 3.1415926535

vec2 rotate(vec2 original, float angle, vec2 pivot){
	mat2 rotation = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
	vec2 final = original;
	final -= pivot;
	final *= rotation;
	final += pivot;
	return final;
}

vec3 simplexGrid (vec2 uv) {
	vec3 xyz = vec3(0.0);

	float f2d = 0.3660254038; //(sqrt(3.) - 1.) / 2.;
	f2d *= (uv.x + uv.y);

    vec2 skew = vec2(0.0);

	skew.x = uv.x + f2d;
	skew.y = uv.y + f2d;

    //skew.x = 1.1547 * uv.x;
	//skew.y = uv.y + 0.5 * skew.x;


	if (skew.x > skew.y) {
		xyz.xy = 1.0 - vec2(skew.x, skew.y - skew.x);
		xyz.z = skew.y;
	}
	else {
		xyz.yz = 1.0 - vec2(skew.x - skew.y, skew.y);
		xyz.x = skew.x;
	}

	return xyz;
}

float scale = 10.;
float lineSize = 0.05;
float lineBlur = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

	uv = rotate(uv, -PI/4., vec2(0.));

	uv *= scale;

    vec3 uvT = simplexGrid(uv);
	vec3 cellCoord = floor(uvT);
	vec3 cellUv = fract(uvT);

    vec3 col = vec3(0.0);
	col = cellUv;
	col = cos(cellCoord+iTime) * 0.5 + 0.5;

	// outline
    for (int i = 0; i < 3; i++) {
        col *= smoothstep(lineSize / 2. - lineBlur, lineSize / 2. + lineBlur, cellUv[i]);
        col *= smoothstep(lineSize / 2. - lineBlur, lineSize / 2. + lineBlur, 1. - cellUv[i]);
    }

    fragColor = vec4(col,1.0);
}
