//blue dot is last clicked pos
//red is current mouse pos
//green is dragged mouse pos

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec4 m = iMouse/iResolution.y;
    float pxunit = 1.0/iResolution.y;
    //calculated in buffer a
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy/iResolution.y;

    vec3 col = texture(iChannel1, uv + draggedPos, 0.0).rgb;

    col = mix(vec3(0,1,0), col, smoothstep(0.01, 0.01 + pxunit*2.0, distance(uv, draggedPos)));
    col = mix(vec3(1,0,0), col, smoothstep(0.01, 0.01 + pxunit*2.0, distance(uv, m.xy)));
    col = mix(vec3(0,0,1), col, smoothstep(0.01, 0.01 + pxunit*2.0, distance(uv, abs(m.zw))));

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = vec4(0);
    if (floor(fragCoord) == vec2(1,0)) {
        vec2 prevpos = texelFetch(iChannel0, ivec2(1,0), 0).zw; //previous dragged mouse pos
        vec4 mp = texelFetch(iChannel0, ivec2(2,0), 0); //prev mouse pos
        vec4 m = iMouse; //current mouse pos

        //if mouse button up
        if (m.z < 0.0 && mp.z >= 0.0){
            col = texelFetch(iChannel0, ivec2(1,0), 0).xyxy;
        } else if (m.z >= 0.0){
            col = vec4(m.xy - abs(m.zw) + prevpos, prevpos);
        } else {
            col = texelFetch(iChannel0, ivec2(1,0), 0);
        }
    }
    //the last mouse position
    if (floor(fragCoord) == vec2(2,0)) {
        col = iMouse;
    }
    fragColor = col;
}
