vec3 grid(vec2 p) {
    vec3 col = vec3(0.);

    col.g = step(abs(p.x), .01);
    col.r = step(abs(p.y), .01);

    vec2 q = p;
    q.x = fract(10. * q.x);
    col += .2*vec3(step(abs(q.x), 0.05));

    q = p;
    q.y = fract(10. * q.y);
    col += .2*vec3(step(abs(q.y), 0.05));

    return col;
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}




