void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 st = fragCoord.xy/iResolution.xy;

    float x = (st.x + iTime)*10.0;
  //  x = (x -3.0)*2.0;
    float y = (sin(x) + 3.0)/5.0;

   vec2 ip = st*3.0-vec2(2.0,1.1);

//    float y2 = sdCircle(ip,0.08);


    vec3 color = vec3(0);

    // Plot a line    
    float pct = plot(st,y);
    pct = max(pct,plotx(st,round(x/0.1)/10.0));
  //  pct = max(pct,plot(st,y2));

    color = (1.0-pct)*color+pct*vec3(0.0,1.0,0.0);

	fragColor = vec4(color,1.0);
}
