void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy *2.0 -1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    vec2 pixelSize = vec2(1.0/iResolution.x, 1.0/iResolution.y);

    float fade = pixelSize.x*5.0;
    float thickness = iMouse.x / iResolution.x - fade;

    float distance = 1.0 - dot(uv,uv);
    vec3 col = vec3(smoothstep(0.0, fade, distance));
    col *= vec3(1.0 - smoothstep(thickness, thickness+fade, distance));

    fragColor.rgb = col;
}
