#define SHIFT_AMOUNT 0.009
#define SHIFT_SPEED 0.25
#define SHIFT_WIDTH 50.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy / iResolution.xy;

   float scanStart = mod(SHIFT_SPEED - iTime / 4.5, 1.0);

   float distanceInScan = (uv.y - scanStart);

   float isInScan = step(scanStart, uv.y);

   float scanOffset = isInScan * sin(7.0 * SHIFT_WIDTH * distanceInScan + radians(100.0))
                               * exp(.05 - SHIFT_WIDTH * distanceInScan);

   uv.x += SHIFT_AMOUNT * scanOffset;

   fragColor = vec4(texture(iChannel0, uv).rgb, 1.0);
}
