    bool circle(vec2 d, vec2 O, float r){
      float coefY = iResolution.y / iResolution.x;
      float X, Y;
       X = d.x - O.x;
       Y = d.y - O.y;
        return X*X + Y*Y*coefY*coefY < r*r;
    }
            float line(vec2 a, vec2 b, vec2 v) {
              return (v.x - a.x)*(b.y - a.y) - (v.y - a.y)*(b.x - a.x);
            }

        bool check(vec2 a, vec2 b, vec2 c, vec2 v) {
          return line(a, b, c) * line(a, b, v) >= 0.0;
        }

    bool triangle(vec2 a, vec2 b, vec2 c, vec2 v) {
      return  check(a, b, c, v) && check(b, c, a, v) && check(c, a, b, v);
    }  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord / iResolution.xy;
   float x = uv.x;
   float y = uv.y;
  fragColor = vec4(0.9, 0.9, 1.0, 1.0);

     if(circle(uv, vec2(0.5, 0.5), 0.12))
       fragColor = vec4(1.0, 1.0, 0.9, 1.0);
     if(circle(uv, vec2(0.55, 0.56), 0.023))
       fragColor = vec4(.0, .0, .0, 1.0);
     if(circle(uv, vec2(0.547, 0.577), 0.023))
       fragColor = vec4(1.0, 1.0, 0.9, 1.0);
     if(circle(uv, vec2(0.5, 0.46), 0.03))
       fragColor = vec4(1.0, 0.0, 1.0, 1.0);
     if(circle(uv, vec2(0.565, 0.36), 0.02))
       fragColor = vec4(1.0, 0.0, 0.5, 1.0);
     if(circle(uv, vec2(0.569, 0.37), 0.02))
       fragColor = vec4(1.0, 1.0, 0.9, 1.0);
     if(triangle(vec2(0.78, 0.5), vec2(0.6, 0.55), vec2(0.6, 0.45), vec2(x, y)))
       fragColor = vec4(1.0, 1.0, 0.9, 1.0);
     if(triangle(vec2(0.39, 0.65), vec2(0.61, 0.65), vec2(0.5, 0.95), vec2(x, y)))
       fragColor = vec4(1.0, 0.0, 0.0, 1.0);  

   uv -= 0.5;
   float x1 = uv.x;
   float y1 = uv.y;

     if((abs(x1) < 0.025) && (y1 < -0.15))
       fragColor = vec4(1.0, 1.0, 0.9, 1.0);
     if(y1 < pow(3.7*x1, 2.0)*(-1.0) - 0.3)
       fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}
