renderpass.code:
vec3 cols[4] = vec3[4](
    vec3(0, 0, 1),
    vec3(0, 1, 0),
    vec3(1, 0, 0),
    vec3(0, 0, 0)
);

vec3 getVal(ivec2 origin)
{
    vec3 outCol = vec3(0);
    for (int shift = 0;shift < 32;shift++)
    {
        int i = (origin.x >> shift) & 1;
        int j = (origin.y >> shift) & 1;
        vec3 col = cols[i + j * 2] * pow(0.5, float(shift + 1));
        outCol += col;
    }
    return outCol;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    ivec2 orig = iMouse.z > 0.0 ? ivec2(fragCoord / iMouse.x) : ivec2(fragCoord * abs(cos(iTime / 2.0)));
    vec3 col = getVal(orig);
    fragColor = vec4(col, 1.0);
}
