#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)

void mainImage(out vec4 O, vec2 C)
{
    vec3 r=iResolution,c=vec3(0),
    d=normalize(vec3(C-.5*r.xy,r.y));
    float i=0.,s,e,g=0.,t=iTime;
	for(;i++<90.;){
        vec4 p=vec4(R(g*d,normalize(H(t*.7)),g*.1),.2 );
        p.z+=t*.4;
        p=sin(sin(p));
        s=1.;
        for(int i=0;i++<6;p=p*e-vec4(1,.3,.5,1.7))
            p=abs(p),
            p=p.x<p.y?p.zwxy:p.zwyx,
            s*=e=2.7/min(dot(p,p/2.),2.);
        g+=e=abs(p.w)/s+1e-4;
	    c+=mix(vec3(0.9),H(log(s)*.9),.4)*.04/exp(i*i*e);
	}
	c*=c*c;
    O=vec4(c,1);
}
