vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float li = (texture(iChannel0, uv * 0.08 +iTime*0.008).r *2. -5.);

    float v = uv.y + li * 0.1;
    v =1. - abs(v * 2.0 - 1.);
    v = pow(abs(v), 2.0 + sin((iTime * 0.15 + li * 0.2)));

    vec3 startColor = vec3(0.05, 0.1, 0.1);
    vec3 endColor = vec3(0., 0.15, 0.15);

    vec3 col = mix(startColor, endColor, smoothstep(0.0, 1.0, uv.y));


    float x = (1. - uv.x * 0.6)*0.6;
    float y = (1.0 - abs(uv.y * 2.0 - 1.0))*1.5;
    col += vec3(x *0.4, y, x) * v*1.2;

    col -= (hash32(fragCoord+iTime))*.2;//noise

    fragColor= vec4(col,.5);


}
