vec2 quadraticInterpolation(in vec2[3] knots, float t)
{
    float tt = t * t;
    float w0 = 2.0 * tt - 3.0 * t + 1.0;
    float w1 = -4.0 * tt + 4.0 * t;
    float w2 = 2.0 * tt - t;

    return w0 * ctrls[0] + w1 * ctrls[1] + w2 * ctrls[2];
}
vec2 BezierQuadraticInterpolation(vec2[3] ctrls, float t)
{
    float f = 1.0 - t;
    float ff = f * f;

    return ff * ctrls[0] + t * (2.0 * f * ctrls[1] + t * ctrls[2]);
}
/**/

void calculateForwardDifferences(
    in vec2[3] ctrls,
    inout vec2[3] fwdDiffs,
    in float dt)
{
    /* Coefficients of quadratic formula: a*x*x + b*x + c 
       This coefficients derived for formulas in function
       quadraticInterpolation, by simple substituon and
       rearranging.
       You can derive coefficients for other quadratic
       interpolation formula or quadratic spline segment. */
    vec2 a = 2. * (ctrls[0] - 2. * ctrls[1] + ctrls[2]);
    vec2 b = 4. * ctrls[1] - 3. * ctrls[0] - ctrls[2];
    vec2 c = ctrls[0];
    /* Coefficients for formulas in function BezierQuadraticInterpolation. *
    vec2 a = ctrls[0] - 2. * ctrls[1] + ctrls[2];
    vec2 b = 2. * (ctrls[1] - ctrls[0]);
    vec2 c = ctrls[0];
    /**/


    // This formulas are same for any quadratic curve.
    fwdDiffs[0] = c;
    fwdDiffs[1] = dt * (b + a * dt);
    fwdDiffs[2] = 2. * a * dt * dt;
}

vec2 find_closest_curve_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    const int num_of_points = 100;
    float dt = 1.0 / float(num_of_points);

    vec2 fwdDiffs[3];
    calculateForwardDifferences(ctrls, fwdDiffs, dt);

    int i = 0;
    do
    {
        vec2 P = fwdDiffs[0];

        vec2 diff = P - NDC;
        float sqDist = dot(diff, diff);

        minSqDist = min(minSqDist, sqDist);

        closestP = 
            minSqDist == sqDist ? 
                P :
                closestP;


        fwdDiffs[0] += fwdDiffs[1];
        fwdDiffs[1] += fwdDiffs[2];   

        ++i;
    } while (i < num_of_points);

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_curve_point_to_pixel(ctrls, NDC),
        NDC,
        0.01,
        0.011));
}

vec2 find_closest_control_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    for (int k = 0; k < num_of_ctrls; ++k)
    {
        vec2 diff = ctrls[k] - NDC;
        float sqDist = dot(diff, diff);

        minSqDist = min(minSqDist, sqDist);

        closestP = 
            minSqDist == sqDist ? 
                ctrls[k] :
                closestP;
    }

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_control_point_to_pixel(ctrls, NDC),
        NDC,
        0.02,
        0.018));
}

vec2 find_closest_convex_hull_point_to_pixel(
    in vec2[num_of_ctrls] ctrls,
    in vec2 NDC)
{
    vec2 closestP = vec2(0);
    float minSqDist = INITIALLY_FAR;

    int num_of_lines = num_of_ctrls - 1; // 2 knots per line

    for (int l = 0; l < num_of_lines; ++l)
    {
        for (float t = 0.; t <= 1.; t += 0.1)
        {
            vec2 P = ctrls[l] * (1. - t) + ctrls[l + 1] * t;

            vec2 diff = P - NDC;
            float sqDist = dot(diff, diff);

            minSqDist = min(minSqDist, sqDist);

            closestP = 
                minSqDist == sqDist ? 
                    P :
                    closestP;
        }
    }

    return closestP;
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = TO_CENTERED_NDC(SC);

#ifdef RANDOM
    //randomize_ctrls(iTime);
    randomize_ctrls2(iTime);
#endif

    O = vec4(circleSQSDF(
        find_closest_convex_hull_point_to_pixel(ctrls, NDC),
        NDC,
        0.008,
        0.0088));
}
