void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    vec2 z = vec2(0.0);
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = cPow(abs(cSqr(z) - uv), (sin(iTime) + 2.0));
        if(dot(z, z) > 65536.0) break;
    } // Divide iterations by maximum
    fragColor = vec4(vec3(sqrt(l / 100.0)) * vec3(0.1, 1.0, 0.8), 1.0);
}

float cAbs(vec2 z) {
    return length(z);
}

float cArg(vec2 a) {
    return atan(a.y, a.x);
}

vec2 cSqr(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y);
}

vec2 cMul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 cExp(vec2 z) {
    return exp(z.x) * vec2(cos(z.y), sin(z.y));
}

vec2 cLog(vec2 a) {
    return vec2(log(cAbs(a)), cArg(a));
}

vec2 cPow(vec2 z, vec2 a) {
    return cExp(cMul(cLog(z), a));
}

vec2 cPow(vec2 z, float a) {
    return cExp(cLog(z) * a);
}
