#define PIXEL_SIZE 16.00

void mainImage(out vec4 f_color, vec2 f_position)
{
    vec2 uv = 
        floor(f_position.xy / PIXEL_SIZE) // map coords to smaller grid (scale down) and throw away fractional part
        * PIXEL_SIZE                      // map coords back to [0.5; resolution.xy - 0.5] 
        / iResolution.xy;                 // map from [0.5; resolution.xy - 0.5] to [0.0; 1.0]

    f_color = texture(iChannel0, uv);
}
