const float PI = 3.14;
const float e = 2.71;

const vec2 ellipse = vec2(2.5, 1.2);
const vec2 decay = vec2(.01, .03);

void mainImage( out vec4 O, in vec2 u )
{
    vec2 U = ( 2. * u - iResolution.xy ) / iResolution.y;
    O = vec4(0.);
    float r = length(U);
    float angle = atan(U.y/U.x);
    float er = 0.5 * (ellipse.x * ellipse.y)/sqrt(ellipse.x * ellipse.x * sin(angle) * sin(angle) + ellipse.y * ellipse.y * cos(angle) * cos(angle));
    float sy = er * abs(sin(angle)) * sin(15. * U.x + 2. * iTime);
    vec3 C0 = vec3(exp((-abs(U.y - sy) + decay.x)/decay.y));
    vec3 C1 = vec3(clamp(exp((-abs(r) + er)/decay.y), 0., 1.));
    vec3 C2 = vec3(clamp(exp((-abs(r - er) + decay.x)/decay.y), 0., 1.));

    O = vec4(C0, 1.) * vec4(C1, 1.) + sin(iTime) * vec4(C2, 1.);
}
