const float pi = 3.14159265;

vec2 rot(in vec2 uvi, in float x, in vec2 o)
{
    float t = radians(x*360.);
    vec2 uv = uvi-0.5-o;
    uv *= mat2(cos(t),-sin(t),sin(t),cos(t));
    return uv+0.5+o;
}

float ease (in float x){
    return pow(x,2.)*(3.-2.*x);
}

float circ (in vec2 uvi, in float s, out float b, out float f) {
    vec2 uv = uvi-0.5;
    float edge = 2.5+sin(iTime*pi+(uvi.y*8.)+(uv.x*sin(iTime*pi*0.5)*4.))/4.;
    float o = 1.-sqrt(uv.x*uv.x+uv.y*uv.y)*edge*0.9;
    f = o;
    float t = s/edge;
    float e = (o+1./t)*t-0.5;
    float bnd = 1.;
    if (e < 0. || e > 1.) bnd = 0.;
    b = ease((1.-abs(e*2.-1.))*bnd);
    return clamp(sign(o),0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 mp = iMouse.xy/res-0.5;
    vec2 uv = fragCoord/res;
    vec2 sq = fragCoord/res.yy;
    sq.x -= res.y/res.x/1.5;
    float cb, cf;
    vec2 r = rot(uv,circ(sq, 30., cb, cf)*((fract(iTime/4.)*360.)/360.),mp);

    vec3 col = texture(iChannel0,r).rgb;
    col = mix(col,vec3(0.2,0.1,0.0),cb)*clamp(cf/2.+0.7,0.,1.);

    fragColor = vec4(col,1.0);
}
