bool in_rec(vec2 corner,vec2 size,vec2 inp)
{
    if(lessThan(corner,inp).x==true&&
    lessThan(corner,inp).y==true&&
    lessThan(inp,corner+size).x==true&&
    lessThan(inp,corner+size).y==true)
    return true;
    else return false;
}
bool oilmeat(vec2 corner,float r,vec2 inp)
{
    if((inp.x*inp.x+inp.y*inp.y/1.5<r*r)&&
    inp.x>corner.x&&
    inp.y>corner.y)
    return true;
    else return false;
}
bool pasta(vec2 corner,float r1,float r2,vec2 inp)
{
    if(oilmeat(corner,r1,inp)==false&&
    oilmeat(corner,r2,inp)==true)
    return true;
    else return false;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv_old = fragCoord/iResolution.yy-vec2(1.77777/2.0,0.5);
    uv_old=uv_old*vec2(10,10);

    vec2 uv=abs(uv_old);

    if(uv.x*uv.x*uv.x+uv.y*uv.y*uv.y<1.0)
    {
        if(in_rec(vec2(0.45,-0.4),vec2(0.2,0.8),uv_old)||
        in_rec(vec2(-0.25,-0.4),vec2(0.2,0.45),uv_old)||
        in_rec(vec2(0.1,-0.4),vec2(0.2,0.55),uv_old)||
        in_rec(vec2(-0.6,-0.4),vec2(0.2,0.8),uv_old)||
        in_rec(vec2(-0.6,0.2),vec2(0.6,0.2),uv_old)||
        pasta(vec2(0,0.1),0.15,0.33,uv_old)
        )
        fragColor = vec4(1.0);
        else fragColor = vec4(1.0,0.4,0.035,1.0);
    }
    else fragColor=vec4(1,1,1,1);
}
