#define MAX_DIST 30.0
#define MIN_DIST 0.001

mat2 rot(float a){
    float s=sin(a);
    float c=cos(a);
    return mat2(c,-s,s,c);
}

float rand(float n){
	return fract(sin(dot(vec2(n),vec2(12.9898,4.1414)))*43758.5453);
}


float SphereDf(vec3 p,vec3 spherePos,float radius,out vec3 color){

	vec2 uv=vec2(p.x+iTime*0.05,p.y);

	vec3 displacement=vec3(texture(iChannel0,uv));

    color=vec3(uv,1.0);
    color.yz*=rot(iTime);

    vec3 color1=texture(iChannel1,uv).xyz;
    color1.xy*=rot(iTime);

    color+=color1;

	float dist=length(spherePos+p)-radius;
	dist+=dot(displacement,displacement)*2.0;
	return dist;
}

float sceneDf(vec3 p,out vec3 color){

	vec3 spherePosition=vec3(0.0);

    float t=(0.5*fract(iTime*0.2)*10.0)-0.5;
	float dist=SphereDf(p,spherePosition,t,color);
	return dist;
}

vec3 calcNormal(vec3 p){
	float e=0.3;
	vec3 normal;
	vec3 i;
	normal.x=sceneDf(vec3(p.x+e,p.y,p.z),i)-sceneDf(vec3(p.x-e,p.y,p.z),i);
	normal.y=sceneDf(vec3(p.x,p.y+e,p.z),i)-sceneDf(vec3(p.x,p.y-e,p.z),i);
	normal.z=sceneDf(vec3(p.x,p.y,p.z+e),i)-sceneDf(vec3(p.x,p.y,p.z-e),i);

	return normalize(normal);
}

vec3 processLightning(vec3 baseColor,vec3 eyePos,vec3 surfacePoint){
	vec3 diffuseDir=normalize(vec3(-1.0,0.0,-1.0));

	vec3 normal=calcNormal(surfacePoint);
	float diffDot=max(dot(-diffuseDir,normal),0.0);
	return 0.1+vec3(diffDot)*baseColor;
}

vec3 marchRay(vec3 eyePos,vec3 dir){
	vec3 color=vec3(0.0);
	vec3 currentPoint=eyePos;
	while(length(currentPoint)<MAX_DIST){
		float intensity;
		vec3 sceneCol;
		float dist=sceneDf(currentPoint,sceneCol);
		if(dist<=MIN_DIST){
			color=processLightning(sceneCol,eyePos,currentPoint);
			break;
		}
		currentPoint+=(dist*dir);
	}
	return color;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec3 eye=vec3(0.0,0.0,6.0);
	vec3 lookat=vec3(0.0);
	vec3 forward=normalize(lookat-eye);
	vec3 right=normalize(cross(forward,vec3(0.0,1.0,0.0)));
	vec3 up=normalize(cross(right,forward));

	vec2 p=(-iResolution.xy+2.0*fragCoord)/iResolution.y;


	vec3 dir=normalize(p.x*right+p.y*up+2.5*forward);
	vec3 color=marchRay(eye,dir);

	fragColor=vec4(color,1.0);
}
