#define ITERS 9
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){
        float scale = 2.5;
        float scale1 = 1.15;
        float s1 = scale1*scale;
        col_prev = col;
        vec2 uv1 = vec2(0.);
        for(int i=0;i<ITERS;i++)
        {
            //uv += uv1/s1;
            uv1= fract(-uv-((vec2(uv.x/scale-uv.y/scale1,uv.y/scale+uv.x/scale1)/(scale))))/scale1;
            uv = uv1;
            uv.x *= scale1;
            uv = fract(-uv.yx/s1)*s1;
            uv.y /= -scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        //col[2] = abs((uv.y)+(uv.x)-col[2]);
        //col[2] = fract((uv.y)-(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}
