//wang from https://www.shadertoy.com/view/ldjczd
float wang(uint u)
{
    uint seed = (u*1664525u);

    seed  = (seed ^ 61u) ^(seed >> 16u);
    seed *= 9u;
    seed  = seed ^(seed >> 4u);
    seed *= uint(0x27d4eb2d);
    seed  = seed ^(seed >> 15u);

    float value = float(seed) / (4294967296.0);
    return value;
}

vec2 pointgen(uint i)
{
    return vec2(wang(i), wang(i+10000u)) - vec2(0.5,0.5);
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(3. / 3., 2. / 3., 1. / 3., 3.);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

#define MAXPOINTS 215u

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y*=(iResolution.y/iResolution.x);

    uint minknn = MAXPOINTS;
    float minval = 100000.0;

    float parameter1 = iMouse.x/iResolution.x;
    float parameter2 = iMouse.y/iResolution.y + 1.0;


    for(uint i = 0u; i<MAXPOINTS; i++)
    {
        float mytime = (max(iTime,float(i)) - float(i));
        mytime = mod(mytime,100.0f);
        vec2 pos = pointgen(i)*cos(mytime)*64.0f;
        pos*=0.0125;
        pos += vec2(0.5f,0.25f);
        float len = length(pos-uv); 
        if(len<minval)
        {
            minval = len;
            minknn = i;
        }

    }

    // Time varying pixel color
    vec2 minknnpos = pointgen(minknn);
    vec4 col = vec4(hsv2rgb(vec3(length(minknnpos)*5.0, 0.6,0.8)),1.0);

    fragColor = col;
}
