// http://adrianboeing.blogspot.com/2011/02/ripple-effect-in-webgl.html

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cPos = -1.0 + 2.0 * gl_FragCoord.xy / iResolution.xy;
    cPos -= -1.0 + 2.0 * iMouse.xy / iResolution.xy;
    float cLength = length(cPos);

    vec2 uv = gl_FragCoord.xy/iResolution.xy+(cPos/cLength)*cos(cLength*12.0-iTime*4.0)*0.03;
    vec3 col = texture(iChannel0,uv).xyz;

    fragColor = vec4(col,1.0);
}
