void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord=fragCoord.xy/iResolution.xy*15.0;
    for(int n=1;n<20;n++)
    {
        float i=float(n);
        coord +=vec2(sin(iTime)/i*sin(coord.y+iTime),sin(iTime)/i*sin(coord.x+iTime));
        coord +=vec2(cos(iTime)/i*sin(coord.x+iTime),cos(iTime)/i*sin(coord.y+iTime));

    }


    vec3 color =vec3(0.5*sin(coord.x+iTime)+0.5,0.5*sin(coord.y+iTime)+0.5,0.5*atan(coord.y,coord.x)+0.5);

    fragColor=vec4(color,1.0);


}
