void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

    vec2 center = iResolution.xy * 0.5;

    float distanceToCenter = length(uv-center);

    float circlue = min(iResolution.y,iResolution.x)/2.0; 

    vec4 color = mix(vec4(0.0),vec4(1.0,1.,0.,1.0),step(circlue,distanceToCenter));
    // Output to screen
    fragColor = color;
}
