//Normal Mandelbrot

bool checkEscape(vec2 x){
    return x.x*x.x + x.y*x.y > 4.;
}

//Badmath Mandelbrot

/*bool checkEscape(vec2 x){
    return x.x*x.y > 2.;
}*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.6;
    vec2 uv = (fragCoord/iResolution.xy - .5)*2.*zoom*vec2(iResolution.x/iResolution.y, 1.);

    vec3 col;

    vec2 c = uv;
    vec2 z = vec2(0., 0.);

    int maxI = 20;
    int iterations;
    for(iterations = 0; iterations <= maxI; iterations++){
        z = complexMulti(z, z) + c;
        if(checkEscape(z)){
            break;
        }
    }
    if(iterations < maxI){
        col = prideColors[(iterations+4)-(iterations+4)/6*6];
    }
    else{
        col = progressColors[int(mod(length(z)*4.3,6.))];
    }

    fragColor = vec4(col,1.0);
}

vec3 prideColors[] = vec3[](
    //Vivid Red
    vec3(1., 0., 0.094),
    //Deep Saffron
    vec3(1., .645, .172),
    //Maximum Yellow
    vec3(1., 1., .254),
    //Ao
    vec3(.0, .5, .094),
    //Blue
    vec3(.0, .0, .973),
    //Philippine Violet
    vec3(.523, .0, .488)
);

vec3 progressColors[] = vec3[](
    //White
    vec3(1., 1., 1.),
    //Amaranth Pink
    vec3(.965, .656, .719),
    //Maya Blue
    vec3(.332, .801, .984),
    //Brown
    vec3(.375, .219, .078),
    //Black
    vec3(.0, .0, .0)
);



vec2 complexMulti(vec2 x, vec2 y){
    return vec2(x.x*y.x - x.y*y.y, x.x*y.y + x.y*y.x);
}
