// 0: sqrt
// 1: sin
// 2: atan
// 3: mul
#define TESTMODE 1
#define ITERATION_SCALE 10

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    for (uint i = 0u; i < uint(iResolution.x * float(ITERATION_SCALE)); i++)
    {
#if TESTMODE == 0
        col *= sqrt(col);
#elif TESTMODE == 1
        col *= sin(col);
#elif TESTMODE == 2
        col *= atan(col);
#else
        col *= vec3(-1.0);
#endif
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
