#define PI 3.141592653589793

vec3 triangleWave(vec3 t) 
{
    vec3 f = mod(t, 2.0);

    return vec3(min(f, 2.0 - f));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float xc = fragCoord.x/iResolution.x * 2.0 + iTime / 2.0;

    fragColor = vec4(
        vec3(triangleWave(xc + vec3(1.0, 1.0 / 3.0, -1.0 / 3.0)) * 3.0 - 1.0),
    1.0);
}
