float sdCircle(vec2 uv,float r){
    return length(uv)-r;
}
float sdRect(vec2 uv,float r){
    return max(abs(uv.x),abs(uv.y))-r;
}


//平滑相交、合并图形距离场;a、b是两个距离场，K是要光滑的范围；
float smin(float a,float b,float k){
    float h=clamp(0.5+0.5*(a-b)/k,0.0,1.0);
    return mix(a,b,h)-k*h*(1.0-h);
}

float smax(float a,float b,float k){
    return -smin(-a,-b,k);
}

float scene(vec2 uv){
    float w = 1.0/iResolution.y;
    float d = sdRect(uv - vec2(0.2,0.2),0.2);
    float c = sdCircle(uv - vec2(0.0,0.0),0.3);
    d= smin(d,c,0.04);
    float r = smoothstep(w,-w,d);
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = 1.0/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(1.0);

    col = mix(col,vec3(1.0,0.0,1.0),scene(uv));

    fragColor = vec4(col,1.0);
}
