// Fork of "Fork Sum Zero Inverted" by deerfeeder. https://shadertoy.com/view/NdVGzR
// 2021-09-06 22:28:41

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    float s = 80.0*sin(iTime*uv.x*uv.y);
    uv.x=abs(uv.x);
    // Time varying pixel color
    vec3 col = cos(iTime+uv.xyx*vec3(s*sin(iTime*0.01),s*cos(iTime*0.02),s*cos(uv.x+iTime*0.06)));
    col = abs(col);
    // Output to screen
    fragColor = vec4(vec3(col.x*col.y*col.z),1.0);
}
