// readable version below

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 5.* ( u+u - R ) / R.y,
         M = iMouse.xy, D;
    M = length(M)<10. ?  2.*cos(iTime+vec2(0,11)) + sin(2.1*iTime+vec2(0,12))
                      :  5.*( M+M - R ) / R.y, // mouse control
    O-=O;
    for( int k=0; k<25; k++) {
        float d = dot( D = vec2(k%5,k/5)-2. - fract(M), D);
        d > 1. && d < 4.                       // draw AA line
            ? O += max(0., 1.-R.y/15.* length( U-M - D * clamp( dot(U-M,D)/d, 0.,1.) ) )
            : O;
     }
}






/** // --- 407 chars

#define S(v) smoothstep(15./R.y,0.,v)          // antialiasing

float L(vec2 p, vec2 a,vec2 b) {               // draw line
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );                       
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 5.* ( u+u - R ) / R.y, I = floor(U),
         M = iMouse.xy, J;
    M = length(M)<10. ? 2.*cos(iTime+vec2(0,11)) + sin(2.1*iTime+vec2(0,12))
                      : 5.*( M+M - R ) / R.y, // mouse control
    O-=O;
    for( int k=0; k<25; k++) {
        J = I + vec2(k%5-2,k/5-2);
        float d = length( J - M );
        d > 1. && d < 2. ?  O += S(L( U, J, M)) : O;
    }
 // O += S(length(U-I));                       // show grid dots
}

