//based on https://www.shadertoy.com/view/XtVXDV

#define dim  vec2(0.25,0.125)


float mask(vec2 uv, vec2 d)
{
    return step(uv.x, d.x)*step(-d.x,uv.x)*step(uv.y,d.y)*step(-d.y,uv.y);
}

vec4 blend_over(vec4 dst, vec4 src)
{
    return dst + (1.0-dst.a)*src.a*src.rgba;
}

vec4 addSprite(vec2 fragCoord, vec2 center, vec2 size, float angle)
{

    // -- prepare uv ratio and translation
 	vec2 uv = center - (fragCoord.xy / iResolution.xy); 
    uv.x *= iResolution.x / iResolution.y; 

    // -- rotate
    float hypot  = sqrt(uv.x * uv.x + uv.y * uv.y);
    float uvang  = atan(uv.y, uv.x);
    float newang = uvang + angle;
    uv = vec2(hypot * cos(newang), hypot * sin(newang));
    return vec4(mask(uv,dim))*texture(iChannel0,((uv/dim)-1.)*0.5);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 col = vec4(0.0); 

    vec4 a = addSprite(fragCoord,
                       vec2(0.5+sin(iTime)*0.5, 0.5+sin(iTime)*0.3),
                       dim,
                       iTime);

    col = blend_over(col,a);


    a = addSprite(fragCoord,
                  vec2(0.5+cos(iTime)*0.5, 0.5+sin(iTime)*0.5),
                  dim,
                  0.);

    col = blend_over(col,a);

    fragColor = col;


}
