precision mediump float;          



            uniform int isVertical;

            uniform float touchX;
            uniform float touchY;            
            #define iMouse vec2(touchX,touchY)



            float iVerticalSpread = 20.0;
            float iHorizontalSpread = 0.05;
            float iSpeed = 0.4;



    void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
                vec2 mouse = iMouse.x==0.?iResolution.xy:iMouse.xy;
                float amountx = 0.0-mouse.x/iResolution.x*0.5;
                float amounty = 0.0+mouse.y/iResolution.y*1.0;
                float POWER = iHorizontalSpread;
                float VERTICAL_SPREAD = iVerticalSpread;
                float ANIM_SPEED = iSpeed;
                vec2 uv = fragCoord.xy / iResolution.xy;
                vec2 uv1 = fragCoord.xy / iResolution.xy;
                float y = 0.;
                if (isVertical == 0) {
                    y = (uv.y + iTime * ANIM_SPEED) * VERTICAL_SPREAD;
                    uv.x += ( 
                        sin(y) 
                         + sin(y * 0.0) * 0.2 
                         + sin(y * 0.0) * 0.03
                        )
                         * POWER
                         * sin(uv.x * 3.14)
                         * sin(iTime);

                } else {
                    y = (uv.x + iTime * ANIM_SPEED) * VERTICAL_SPREAD;
                    uv.y += ( 
                            sin(y) 
                             + sin(y * 0.0) * 0.2 
                             + sin(y * 0.0) * 0.03
                            )
                             * POWER
                             * sin(uv.x * 3.14)
                             * sin(iTime);
                }



                float r = texture(iChannel0, uv).r;
                float g = texture(iChannel0, uv1).g;
                float b = texture(iChannel0, uv).b;
                fragColor = vec4(r,g,b,1.0);
            }
