void  mainImage(out vec4 color,in vec2 fragCoord){
  vec2    coord = fragCoord-1.0*iResolution.xy/2.0;
  float       x = coord.x;
  float       y = coord.y;
  float       t = iTime*4.0;
  float       r = sqrt(x*x+y*y); 
  float      f1 = x*sin(r/t);
  float      f2 = y*cos(r/t);
  int     dream = abs(int(f1)&int(f2))&int(r*r);
  //      dream = abs(int(f1)&int(f2));
  //      dream = int(f1)&int(f2);
  color         = vec4(dream);
  //color       = vec4(1-dream);
  //color       = vec4(dream, abs(int(f1))&abs(int(f2)), 1-dream*dream, 1);
}
