#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g=0.,e;++i<99.;){
        p=g*d;
        p=R(p,vec3(.577),iTime*.1);
        p.z+=iTime*3.;
        p=atan(cos(p));
        g+=e=abs(length(p)-.8+sin(iTime)*.2)+.001;
        O.xyz+=vec3(5,3,2)*.01*exp(-.1*i*i*e);
    }
    O*=O*O*O*O;
}
