float its = 7.0; //Iterations

float sdSquare(in vec2 p)
{
    vec2 d = abs(p)-vec2(0.5,0.5);
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


float map(in vec2 p)
{
    float sd = sdSquare(p);
    for (float it = 1.; it < its; it++)
    {
        vec2 mod_d = mod(p*pow(3.,it)+1.5,3.)-1.5;
        float d = sdSquare(mod_d);
        sd = max(sd, -d);
    }
    return sd;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)y
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;

    //rotate
    float th = iTime*0.5;
    mat2 Rot = mat2(cos(th),sin(th),-sin(th), cos(th));
    uv = Rot*uv;

    // Output to screen
    fragColor = vec4(smoothstep(0.0005,-0.0005,map(uv)),0.0,0.0,1.0);
}
